import React from "react";

interface CastProps {
  character: string;
  id: number;
  name: string;
  image: string;
}

const CastDetails: React.FC<CastProps> = ({ character, name, image }) => {
  const ImageUrl = "https://media.themoviedb.org/t/p/w138_and_h175_face";
  const AvatarUrl =
    "data:image/jpeg;base64,";

  // Determine the photo URL based on whether image is null or not
  const photo = image ? `${ImageUrl}${image}` : AvatarUrl;

  return (
    <div className="flex flex-col w-[200px] h-[200px]">
      <div className="rounded-full">
        <img
          src={photo}
          alt="cast image"
          height={175}
          width={138}
          style={{ borderRadius: "3rem" }}
        />
      </div>
      <div className="text-black font-semibold">{name}</div>
      <div className="text-gray-500">{character}</div>
    </div>
  );
};

export default CastDetails;
